import React from 'react';

function FeatureCards() {
  const features = [
    { title: 'AI-Powered Summarization', desc: 'Get concise, professional summaries of your meetings in seconds' },
    { title: 'Smart Action Items', desc: 'Automatically extract tasks with deadlines and assignees' },
    { title: 'Multiple Export Formats', desc: 'Export to Slack, email, Notion, or Markdown format' }
  ];

  return (
    <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', width: '90%', maxWidth: '1200px' }}>
      {features.map((f, i) => (
        <div key={i} style={{ background: '#fff', padding: '20px', borderRadius: '8px', width: '30%', textAlign: 'center', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
          <h3>{f.title}</h3>
          <p style={{ color: '#7f8c8d' }}>{f.desc}</p>
        </div>
      ))}
    </div>
  );
}

export default FeatureCards;
