import React, { useState } from 'react';
import axios from 'axios';
import ProcessStatus from './ProcessStatus';

function TranscriptInput() {
  const [file, setFile] = useState(null);
  const [fileType, setFileType] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [transcript, setTranscript] = useState('');
  const [utterances, setUtterances] = useState([]);
  const [summary, setSummary] = useState('');
  const [status, setStatus] = useState('Ready to Process');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    const type = selectedFile.type;
    setFile(selectedFile);
    setFileType(type);

    if (type.startsWith("audio/")) {
      setAudioUrl(URL.createObjectURL(selectedFile));
    } else {
      setAudioUrl(null);
    }
  };

  const handleUploadAndTranscribe = async () => {
    if (!file) return alert("Please upload an audio or text file.");

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    setStatus('⏳ Transcribing...');
    setTranscript('');
    setSummary('');
    setUtterances([]);

    try {
      const res = await axios.post('http://localhost:8000/transcribe', formData);
      const transcriptText = res.data.transcript;

      setTranscript(transcriptText);

      // Speaker-wise breakdown
      const lines = transcriptText.split('\n');
      const structured = lines.map(line => {
        const match = line.match(/^(Speaker \d+): (.+)$/);
        return match ? { speaker: match[1], text: match[2] } : null;
      }).filter(Boolean);
      setUtterances(structured);

      setStatus('✅ Transcription completed. Ready for AI processing.');
    } catch (err) {
      console.error(err);
      alert("❌ Error during transcription: " + (err.response?.data?.error || err.message));
      setStatus('❌ Transcription failed');
    } finally {
      setLoading(false);
    }
  };

  const handleUseSample = () => {
    const sample = 'Speaker 1: Hello everyone.\nSpeaker 2: Hi, let\'s begin the meeting.';
    setTranscript(sample);
    setUtterances([
      { speaker: 'Speaker 1', text: 'Hello everyone.' },
      { speaker: 'Speaker 2', text: "Hi, let's begin the meeting." },
    ]);
    setStatus('✅ Sample loaded');
  };

  const handleClear = () => {
    setFile(null);
    setFileType(null);
    setAudioUrl(null);
    setTranscript('');
    setSummary('');
    setUtterances([]);
    setStatus('Ready to Process');
  };

  const handleDownload = () => {
    const blob = new Blob([transcript], { type: 'text/plain' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'transcript.txt';
    link.click();
  };

  const handleProcess = async () => {
    if (!transcript) {
      alert('Transcript is empty.');
      return;
    }

    setStatus('🧠 Sending transcript to LLM...');

    try {
      const response = await fetch('https://api.example.com/summarize', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: transcript }),
      });

      if (!response.ok) throw new Error('API Error');

      const data = await response.json();
      setSummary(data.summary);
      setStatus('✅ Summary ready!');
    } catch (error) {
      console.error(error);
      setStatus('❌ Failed to process summary');
    }
  };

  return (
    <div className="transcript-card" style={{ background: '#fff', padding: '20px', borderRadius: '8px', width: '100%', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
      <h3>📄 Transcript Processor</h3>

      <input
        type="file"
        accept=".mp3,.wav,.m4a,.aac,.ogg,.txt"
        onChange={handleFileChange}
      />
      <button
        onClick={handleUploadAndTranscribe}
        disabled={loading}
        style={{ marginLeft: '10px', padding: '6px 12px' }}
      >
        {loading ? 'Transcribing...' : 'Upload & Transcribe'}
      </button>

      {audioUrl && (
        <div style={{ marginTop: '10px' }}>
          <audio controls src={audioUrl} />
        </div>
      )}

      {transcript && (
        <div style={{ marginTop: '15px' }}>
          <h4>📝 Transcript (editable)</h4>
          <textarea
            placeholder="Transcript will appear here..."
            value={transcript}
            onChange={(e) => setTranscript(e.target.value)}
            style={{ width: '100%', height: '150px', padding: '10px', marginTop: '10px' }}
          />
          <button onClick={handleDownload} style={{ marginTop: '10px', background: '#007bff', color: 'white', padding: '6px 12px', borderRadius: '5px' }}>
            Download (.txt)
          </button>
        </div>
      )}

      {utterances.length > 0 && (
        <div style={{ marginTop: '15px' }}>
          <h4>👥 Speaker-wise View</h4>
          {utterances.map((u, i) => (
            <div key={i} style={{ padding: '8px', marginBottom: '6px', background: '#f4f4f4', borderRadius: '4px' }}>
              <strong>{u.speaker}:</strong> {u.text}
            </div>
          ))}
        </div>
      )}

      <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
        <button onClick={handleUseSample} style={{ background: '#e0e0e0', padding: '8px 12px', borderRadius: '5px' }}>
          Use Sample
        </button>
        <button onClick={handleClear} style={{ background: '#f8d7da', padding: '8px 12px', borderRadius: '5px' }}>
          Clear
        </button>
      </div>

      <button
        onClick={handleProcess}
        style={{ background: '#333', color: '#fff', width: '100%', padding: '12px', marginTop: '15px', borderRadius: '5px' }}
      >
        Process with AI
      </button>

      {/* ✅ ProcessStatus Component */}
      <ProcessStatus status={status} summary={summary} />
    </div>
  );
}

export default TranscriptInput;
