import React from 'react';
import { Mic, FileText, Users, Sparkles } from 'lucide-react';

const features = [
  {
    icon: <Mic size={28} />,
    title: "Audio File Support",
    description: "Upload .mp3, .wav, or .m4a files for instant transcription.",
  },
  {
    icon: <FileText size={28} />,
    title: "Text File Support",
    description: "Upload .txt meeting notes for quick summary generation.",
  },
  {
    icon: <Users size={28} />,
    title: "Speaker-wise View",
    description: "Easily identify who said what using smart speaker tagging.",
  },
  {
    icon: <Sparkles size={28} />,
    title: "AI-Powered Summary",
    description: "Get concise summaries from long transcripts using LLMs.",
  },
];

const FeatureCards = () => {
  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 p-4">
      {features.map((feature, index) => (
        <div
          key={index}
          className="bg-white p-6 rounded-2xl shadow-md hover:shadow-xl transition-all duration-300 border border-gray-200"
        >
          <div className="text-indigo-600 mb-3">{feature.icon}</div>
          <h3 className="text-lg font-semibold mb-1">{feature.title}</h3>
          <p className="text-sm text-gray-600">{feature.description}</p>
        </div>
      ))}
    </div>
  );
};

export default FeatureCards;
