import React, { useState } from 'react';
import axios from 'axios';

const TranscriptInput = () => {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResponse('');
  };

  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file (.mp3, .wav, or .txt)');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://127.0.0.1:8000/transcribe', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setResponse(res.data.transcription || res.data.summary || 'Success!');
    } catch (error) {
      console.error(error);
      setResponse('Error during upload or processing.');
    }
  };

  return (
    <div style={{ width: '45%' }}>
      <h2>Upload Audio or Text File</h2>
      <input
        type="file"
        accept=".mp3,.wav,.txt"
        onChange={handleFileChange}
        style={{ marginBottom: '10px' }}
      />
      <br />
      <button onClick={handleUpload} style={{ padding: '8px 16px', marginBottom: '15px' }}>
        Upload & Process
      </button>
      <div>
        <strong>Response:</strong>
        <p>{response}</p>
      </div>
    </div>
  );
};

export default TranscriptInput;
