function Header() {
  return (
    <header style={{
      backgroundColor: '#2c3e50',
      color: 'white',
      padding: '20px',
      textAlign: 'center',
      boxShadow: '0 2px 10px rgba(0,0,0,0.2)'
    }}>
      <h1 style={{ fontSize: '2rem', marginBottom: '10px' }}>üéôÔ∏è Audio & Text File Transcriber</h1>
      <p style={{ fontSize: '1rem', color: '#ecf0f1' }}>
        Upload your <strong>.mp3 / .wav / .txt</strong> files and get a transcript & summary using AI.
      </p>
    </header>
  );
}

export default Header;
