import React, { useState } from 'react';
import axios from 'axios';

function TranscriptInput() {
  const [file, setFile] = useState(null);
  const [fileType, setFileType] = useState('');
  const [audioUrl, setAudioUrl] = useState(null);
  const [transcript, setTranscript] = useState('');
  const [utterances, setUtterances] = useState([]);
  const [summary, setSummary] = useState('');
  const [status, setStatus] = useState('Ready to Process');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    setFile(selectedFile);
    const ext = selectedFile.name.split('.').pop().toLowerCase();
    setFileType(ext);

    if (['mp3', 'wav', 'm4a', 'aac', 'ogg'].includes(ext)) {
      setAudioUrl(URL.createObjectURL(selectedFile));
    } else {
      setAudioUrl(null);
    }
  };

  const handleUploadAndTranscribe = async () => {
    if (!file) return alert("Please upload an audio or text file.");

    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);
    setStatus('‚è≥ Processing...');
    setTranscript('');
    setSummary('');
    setUtterances([]);

    try {
      const res = await axios.post('http://localhost:8000/transcribe', formData);
      const receivedTranscript = res.data.transcript || '';

      setTranscript(receivedTranscript);

      const lines = receivedTranscript.split('\n');
      const structured = lines.map(line => {
        const match = line.match(/^(Speaker \d+): (.+)$/);
        return match ? { speaker: match[1], text: match[2] } : null;
      }).filter(Boolean);
      setUtterances(structured);

      setStatus('‚úÖ Processing completed. You can now continue.');
    } catch (err) {
      console.error(err);
      alert("‚ùå Error during transcription: " + (err.response?.data?.error || err.message));
      setStatus('‚ùå Failed to process');
    } finally {
      setLoading(false);
    }
  };

  const handleUseSample = () => {
    const sample = 'Speaker 1: Hello everyone.\nSpeaker 2: Hi, let\'s begin the meeting.';
    setTranscript(sample);
    setUtterances([
      { speaker: 'Speaker 1', text: 'Hello everyone.' },
      { speaker: 'Speaker 2', text: "Hi, let's begin the meeting." },
    ]);
    setStatus('Sample loaded');
  };

  const handleClear = () => {
    setFile(null);
    setFileType('');
    setAudioUrl(null);
    setTranscript('');
    setSummary('');
    setUtterances([]);
    setStatus('Ready to Process');
  };

  const handleDownload = () => {
    const blob = new Blob([transcript], { type: 'text/plain' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'transcript.txt';
    link.click();
  };

  const handleProcess = async () => {
    setStatus('üß† Sending transcript to LLM...');
    try {
      const response = await fetch('https://api.example.com/summarize', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: transcript })
      });
      if (!response.ok) throw new Error('API Error');
      const data = await response.json();
      setSummary(data.summary);
      setStatus('‚úÖ Summary ready!');
    } catch (error) {
      console.error(error);
      setStatus('‚ùå Failed to process');
    }
  };

  return (
    <div className="transcript-card" style={{ background: '#fff', padding: '20px', borderRadius: '8px', width: '45%', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
      <h3>üìÑ Transcript Processor</h3>

      <input type="file" accept=".mp3,.wav,.m4a,.aac,.ogg,.txt" onChange={handleFileChange} />
      <button onClick={handleUploadAndTranscribe} disabled={loading} style={{ marginLeft: '10px', padding: '6px 12px' }}>
        {loading ? 'Processing...' : 'Upload & Transcribe'}
      </button>

      {audioUrl && (
        <div style={{ marginTop: '10px' }}>
          <audio controls src={audioUrl} />
        </div>
      )}

      {transcript && (
        <div style={{ marginTop: '15px' }}>
          <h4>üìù Transcript (editable)</h4>
          <textarea
            placeholder="Transcript will appear here..."
            value={transcript}
            onChange={(e) => setTranscript(e.target.value)}
            style={{ width: '100%', height: '150px', padding: '10px', marginTop: '10px' }}
          />
          <button onClick={handleDownload} style={{ marginTop: '10px', background: '#007bff', color: 'white', padding: '6px 12px', borderRadius: '5px' }}>Download (.txt)</button>
        </div>
      )}

      {utterances.length > 0 && (
        <div style={{ marginTop: '15px' }}>
          <h4>üë• Speaker-wise View</h4>
          {utterances.map((u, i) => (
            <div key={i} style={{ padding: '8px', marginBottom: '6px', background: '#f4f4f4', borderRadius: '4px' }}>
              <strong>{u.speaker}:</strong> {u.text}
            </div>
          ))}
        </div>
      )}

      <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
        <button onClick={handleUseSample} style={{ background: '#e0e0e0', padding: '8px 12px', borderRadius: '5px' }}>Use Sample</button>
        <button onClick={handleClear} style={{ background: '#f8d7da', padding: '8px 12px', borderRadius: '5px' }}>Clear</button>
      </div>

      <button onClick={handleProcess} style={{ background: '#333', color: '#fff', width: '100%', padding: '12px', marginTop: '15px', borderRadius: '5px' }}>Process with AI</button>
      <p style={{ marginTop: '10px', color: '#7f8c8d' }}>{status}</p>

      {summary && (
        <div style={{ marginTop: '15px', background: '#f9f9f9', padding: '10px', borderRadius: '5px' }}>
          <strong>Summary:</strong>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}

export default TranscriptInput;
