// src/components/FeatureCards.jsx
import React from 'react';

function FeatureCards() {
  const features = [
    { emoji: 'üéß', title: 'Audio Support', desc: 'Upload and transcribe audio files easily.' },
    { emoji: 'üìù', title: 'Text Upload', desc: 'Paste or upload plain text files (.txt).' },
    { emoji: '‚ö°', title: 'Fast Processing', desc: 'Quickly get transcript and summary with AI.' },
    { emoji: 'üì§', title: 'Export', desc: 'Download the transcript to share or store.' },
  ];

  return (
    <section style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '20px', marginTop: '30px' }}>
      {features.map((f, idx) => (
        <div key={idx} style={{ background: '#fff', borderRadius: '10px', padding: '20px', width: '250px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
          <h2 style={{ fontSize: '2rem' }}>{f.emoji}</h2>
          <h4>{f.title}</h4>
          <p style={{ fontSize: '14px', color: '#555' }}>{f.desc}</p>
        </div>
      ))}
    </section>
  );
}

export default FeatureCards;
